import { useState } from "react";
import logo from "./logo.svg";
import "./App.css";

const App = () => {
  const [todos, setTodos] = useState([]);
  const [todo, setTodo] = useState("");

  function handleInputChange(e){
    setTodo(e.target.value);
  }

  function handleFromSubmit(e){
    if(todo !== ""){
      setTodos(
        ...todos,
        {
          id:todo.length +1,
          text:todo.trim()
        }
      )
    }

    setTodo("")
  }

  return (
    <div className="App">
      <h1>TCSMGMT</h1>

      <form onSubmit={handleFromSubmit}>
        <input type="text" name="todo" placeholder="Create new todo" value={todo}></input>
      </form>

      <ul className="todo-list">
        {todos.map((todo) => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
